var Event = require("../models/event.model.js");

exports.createEvent = function (req, res) {
    var entry = new Event({
        //ID: Number,  -> autogenerated
        Code: req.body.EventCode, //[todo] need to generate it somehow from code
        Title: req.body.EventTitle,
        Description: req.body.EventDescription,
        Status: 'Active'
    });

    entry.save();

    res.render('newevent', { title: 'Event Created ... create New Event' });
}

